import React, { useState } from "react";
import "./style.scss";

let SpeechRecognition =
  window.SpeechRecognition ||
  window.webkitSpeechRecognition ||
  window.speechSynthesis;

let mic = new SpeechRecognition();

mic.continuous = true;
mic.interimResults = true;
mic.lang = "en-US";
mic.crossBrowser = true;

const SpeechToText = () => {
  const [transcript, setTranscript] = useState("");

  const startRecording = () => {
    mic.onresult = (event) => {
      const transcript = Array.from(event.results)
        .map((result) => result[0])
        .map((result) => result.transcript)
        .join("");
      setTranscript(transcript);
      console.log(transcript);
    };
    mic.start();
  };
  const stopRecording = () => {
    mic.stop();
  };

  return (
    <div className="button-container">
      <button className="button" onClick={startRecording}>
        Start Listening
      </button>
      <button className="button" onClick={stopRecording}>
        Stop Listening
      </button>
      <p>{transcript}</p>
      <div className="read-aloud-question">

        <div
          className="answer-main-wrap"
          style={
            userDetails.type === "custom"
              ? { height: "180px" }
              : { height: "150px" }
          }
        >
          <>
            {isListening === false && isContinue === false ? (
              <div className="d-flex">
                {handlecurrentStatus && (
                  <p>
                    Current Status:{" "}
                    <span>
                      beginning in next{" "}
                      {timerSeconds > 9 ? timerSeconds : "0" + timerSeconds}{" "}
                      Seconds.
                    </span>
                  </p>
                )}
              </div>
            ) : (
              <div className="cards-body">
                <p>
                  Current Status: <span>Answer {statusAction}.</span>
                </p>
                <div className="progrss-bar-mic-pm">
                  {userDetails.type === "custom" && (
                    <div className="mic-icon-main">
                      {" "}
                      <FaMicrophoneAlt className="mic-icon" />
                    </div>
                  )}

                  <ProgressBar className="Progress-bar" now={audioRecorder} />
                </div>
                <div className="Recorded-Timer">
                  {recordedSeconds > 9
                    ? recordedSeconds
                    : "0" + recordedSeconds}{" "}
                  Seconds Recorded.
                </div>
              </div>
            )}
            <audio id="myBeepAudio" src={Beep} preload="true" />
            <ReactMic
              record={record}
              className="sound-wave"
              onStop={onStop}
              onData={onData}
              visualSetting="frequencyBars"
              mimeType="audio/ogg"
            />
            {userDetails.type === "custom" && (
              <>
                {isListening === false && isContinue === false ? (
                  handlecurrentStatus && (
                    <Button
                      className="skip-btn"
                      variant="primary"
                      onClick={handleskipTimer}
                      disabled={btnLoading ? true : false}
                    >
                      <span>Skip Timer</span>
                    </Button>
                  )
                ) : (
                  <Button
                    className="skip-btn"
                    variant="primary"
                    onClick={() => handleAnswerAnalysis("Next")}
                  >
                    <span>Stop</span>
                  </Button>
                )}
              </>
            )}
          </>
        </div>
        {userDetails.type === "custom" && timerSecondsForprogress !== 0 ? (
          <div
            className="progress-bar-pm"
            style={{ width: "100%", background: "white", height: "5px" }}
          >
            <div
              style={{
                width: `${timerSecondsForprogress}%`,
                background: "#049b40",
                height: "5px",
              }}
              className={`${
                timerSecondsForprogress !== 100 && "transintion-time"
              }`}
            ></div>
          </div>
        ) : (
          ""
        )}

        <div
          className={`${
            userDetails.type === "custom" && "question-text-speech-s"
          } question-container `}
        >
          <h3
            dangerouslySetInnerHTML={{
              __html:
                questionsData && questionsData[selectedQautionIndex]?.questions,
            }}
          />
          {/* { questionsData[selectedQautionIndex]?.questions} */}
        </div>
      </div>
    </div>
  );
};

export default SpeechToText;
