import React, { useState } from "react";
import "./style.scss";

let SpeechRecognition =
  window.SpeechRecognition ||
  window.webkitSpeechRecognition ||
  window.speechSynthesis;

let mic = new SpeechRecognition();

mic.continuous = true;
mic.interimResults = true;
mic.lang = "en-US";
mic.crossBrowser = true;
let isListening = true;
const SpeechToText = () => {
  const [transcript, setTranscript] = useState("");

  const startRecording = () => {
    // mic.onresult = (event) => {
    //   const speechToText = event.results[0][0].transcript;
    //   setTranscript(speechToText);
    // };
    mic.onresult = (event) => {
      const transcript = Array.from(event.results)
        .map((result) => result[0])
        .map((result) => result.transcript)
        .join("");
      setTranscript(transcript);
      console.log(transcript);
    };
    mic.start();
  };
  const stopRecording = () => {
    mic.stop();
  };
  React.useEffect(() => {
    handleListen();
  }, [isListening]);

  const handleListen = () => {
    if (isListening) {
      mic.start();
      mic.onend = () => {
        mic.start();
      };
    } else {
      mic.stop();
      mic.onend = () => {
        // setMicOnOffStatus(false);
      };
    }
    mic.onstart = () => {};
    mic.onresult = (event) => {
      const transcript = Array.from(event.results)
        .map((result) => result[0])
        .map((result) => result.transcript)
        .join("");

      textAnswer = transcript;
      console.log(transcript, "transcript");
      let audioToText = [];
      audioToText.push(transcript);
      if (browserName === "Edge") {
        if (
          pauseTime >= 2 &&
          countWords(transcript) !== 1 &&
          (findWordBeforeLastDot(transcript).includes(".") ||
            findWordBeforeLastDot(transcript).includes("?"))
        ) {
          const regex = new RegExp(findWordBeforeLastDot(transcript), "g");
          pauseArry.push({
            time: pauseTime,
            space: countWords(transcript) - 1,
            transcript,
            lastWord: findWordBeforeLastDot(transcript),
            searchDuplicate: (transcript.match(regex) || []).length,
          });
          setPauseArry(pauseArry);
        }
      } else {
        if (pauseTime >= 2 && countWords(transcript) !== 1) {
          pauseArry.push({
            time: pauseTime,
            space: countWords(transcript) - 1,
            transcript,
          });
          setPauseArry(pauseArry);
        }
      }

      pauseTime = 0;

      mic.onerror = (event) => {
        let audioToTextString = audioToText && audioToText.join(" ");
      };
    };
  };

  return (
    <div className="button-container">
      <button className="button" onClick={startRecording}>
        Start Listening
      </button>
      <button className="button" onClick={stopRecording}>
        Stop Listening
      </button>
      <p>{transcript}</p>
    </div>
  );
};

export default SpeechToText;
